export interface ContractArtifact {
  contractName: string
  abi: any[]
  bytecode: string
}

export const getContractArtifact = (contractName: string): ContractArtifact | null => {
  switch (contractName) {
    case 'SimpleERC20':
      return {
        contractName: 'SimpleERC20',
        abi: SimpleERC20_ABI,
        bytecode: '' // Hardhat Ignitionを使用するためbytecodeは不要
      }
    case 'SimpleERC721':
      return {
        contractName: 'SimpleERC721',
        abi: SimpleERC721_ABI,
        bytecode: '' // Hardhat Ignitionを使用するためbytecodeは不要
      }
    default:
      return null
  }
}